
public class alphabetPattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j,n=5;
		char c='A';
		for(i=1;i<=n;i++)
		{
			//c='A';
			for(j=1;j<=i;j++)
			{
				System.out.print(c +" ");
				c++;
				
			}
			System.out.println();
			
		}

	}

}
